mapper.select:
WITH user_no AS (SELECT count(user_info_user_no), user_info_user_no FROM user_traffic_log WHERE log_content like 'addBookmark' AND DATE_FORMAT(log_datetime, "%Y-%m-%d") = CURDATE() GROUP BY user_info_user_no)
SELECT hour(log_datetime) as log_hour, count(*) as log_cnt FROM user_no n INNER JOIN user_traffic_log u on u.user_info_user_no = n.user_info_user_no WHERE log_content like '%ExLink' AND DATE_FORMAT(log_datetime, "%Y-%m-%d") = CURDATE() GROUP BY 1 ORDER BY 1;
SELECT date(log_datetime) as log_date, count(*) as log_cnt FROM user_no n INNER JOIN user_traffic_log u on u.user_info_user_no = n.user_info_user_no WHERE log_content like '%ExLink' AND log_datetime BETWEEN DATE_ADD(CURDATE(), INTERVAL -1 week) AND NOW() GROUP BY 1 ORDER BY 1;
SELECT date(log_datetime) as log_date, count(*) as log_cnt FROM user_no n INNER JOIN user_traffic_log u on u.user_info_user_no = n.user_info_user_no WHERE log_content like '%ExLink' AND log_datetime BETWEEN DATE_ADD(CURDATE(), INTERVAL -1 month) AND NOW() GROUP BY 1 ORDER BY 1;
